import React, {useState, useEffect} from 'react';
import Formulario from "./components/Formulario";
import ListImages from "./components/ListImages";

function App() {

  // state de la App
  const [search, setSearch] = useState('');
  const [images, setImages] = useState([]);
  const [page, setPage] = useState(1);
  const [totalpage, setTotalpage] = useState();

  useEffect(() => {
    const consultAPI = async () => {
      if(search === '') return;

      const cantImagePage = 30;
      const key = "17476031-41ab14d4d1084eb8ceb536ae7";
      const url = `https://pixabay.com/api/?key=${key}&q=${search}&per_page=${cantImagePage}`;

      const request = await fetch(url);
      const result = await request.json();

      setImages(result.hits);
    }
    consultAPI();

  }, [search])

  return (
    <div className="container">
      <div className="jumbotron">
        <p className="lead text-center">Search Images</p>

        <Formulario
          setSearch={setSearch}
        />
      </div>

      <div className="row justify-conent-center">
        <ListImages
          images={images}
        />
      </div>
    </div>
  );
}

export default App;
