import React, {Fragment, useState, useEffect} from 'react';
import Formulario from "./components/Formulario";
import Axios from "axios";

function App() {

  // definir el state
  const [searchLyric, setSearchLyric] = useState({})

  useEffect(() => {
    // comprobamos si el objeto esta vacio
    if(Object.keys(searchLyric).length === 0) return;

    const consultApiLyric = async () => {
      const {artist, lyric} = searchLyric;
      const url = `https://api.lyrics.ovh/v1/${artist}/${lyric}`;

      const result = await axios(url)
    }

  },[searchLyric])

  return (
    <Fragment>
      <Formulario
        setSearchLyric={setSearchLyric}
      />
    </Fragment>
  );
}

export default App;
