  const { strDrink, strDrinkThumb } = recipe;

  return (
    <div className="col-md-4 mb-3">
      <div className="card">
        <h2 className="card-header">{strDrink}</h2>

        <img src={strDrinkThumb} className="card-img-top" alt={`Image de ${strDrink}`}/>

