import React, {Fragment, useState, useEffect} from 'react';
import Formulario from "./components/Formulario";
import Cancion from "./components/Cancion";
import axios from "axios";

function App() {

  // definir el state
  const [searchLyric, setSearchLyric] = useState({})
  const [lyric, setLyric] = useState('')

  useEffect(() => {
    // comprobamos si el objeto esta vacio
    if(Object.keys(searchLyric).length === 0) return;

    const consultApiLyric = async () => {
      const {artist, song} = searchLyric;
      const url = `https://api.lyrics.ovh/v1/${artist}/${song}`;

      const result = await axios(url);
      console.log(result)

      setLyric(result.data.lyrics);
    }
    consultApiLyric();
  },[searchLyric])

  return (
    <Fragment>
      <Formulario
        setSearchLyric={setSearchLyric}
      />

      <div className="container mt-5">
        <div className="row">
          <div className="col-md-6">
          </div>
          <div className="col-md-6">
            <Cancion
              lyric={lyric}
            />
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default App;
